package repo1;

public class ShiftOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = -10;

		System.out.println(i>>>2);
		System.out.println(i>>>30);
		System.out.println(i>>>32);
		
		
		byte b1 = 10;
		System.out.println(b1>>1);
		System.out.println(b1<<1);
		System.out.println(b1>>>1);

	}

}
